import React, {Component} from 'react';

import tasks from './samples/task.json';

import Tasks from './components/Tasks'

class App extends Component{

  state ={
    task:tasks
  }

  render(){
  return <div>
    { this.state.task.map(e => <p key={e.id}>
      {e.title} - {e.description} - {e.done} - {e.id}
      <Tasks/>
    </p>)
    }
  </div>
  }
}
export default App;
