import React, {Component} from 'react';

export default class TaskForm extends Component{

    state = {
        title:'',
        description:''
    }
    onSubmit = e => {  //Se ejecuta cuando escucha el evento de envío del formulario
        e.preventDefault();
    }

        onChange= e => {
            console.log(e.target.value)
        }
    render (){
        return (
            <form onSubmit={this.onSubmit}>
                <input type="text" placeholder="Write a Task" onChange={this.onChange} value={this.state.title}/>
                <br/>
                <textarea placeholder="Write a description" onChange={this.onChange} value={this.state.description}></textarea>
                <br></br>
                <button type="submit">Save Task</button>
            </form>
        )
    }
}
